#include "RealignTool.h"

#include <string>
#include <iostream>

#include "SnowTools/BWAWrapper.h"
#include "SnowTools/BamRead.h"

#define _DIVBWT 1
#define FILE_DUMP 1

namespace opt {

  std::string refgenome = "/seq/references/Homo_sapiens_assembly19/v1/Homo_sapiens_assembly19.fasta";
}

void runRealign(int argc, char** argv) 
{

  SnowTools::USeqVector v = {
    {"TP53", ""},
    {"MYC", ""}
  };

  SnowTools::BWAWrapper w;
  w.constructIndex(v);
  w.writeIndexToFiles("myidx.fa");
  w.retrieveIndex(opt::refgenome);

  std::string test_seq = "CACGACGGTGACACGCTTCCCTGGATTGGGTAAGCTCCTGACTGAACTTGATGAGTCCTCTCTGAGACTATAAACCCTAATTTTTTTTATTTAAGTACATTTTGCTTTTTAAAGTTGATTTTTTTCTATTGT";
  std::string name = "name1";

  SnowTools::BamReadVector brv;
  w.alignSingleSequence(test_seq, name, brv, true);
  
  for (auto& i : brv)
    std::cerr << i << std::endl;
  
  std::cout << "DONE" << std::endl;
}
